(59,	'amazon-mcf-reviews-2023-12-07',	'marketing',	'en_US',	'Enjoying Amazon MCF for WooCommerce?',	'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!',	'{}',	'pending',	'woocommerce.com',	'2024-04-07 17:44:18',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(60,	'remove_estimated_deposits_2024',	'marketing',	'en_US',	'Estimated deposits are going away',	'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.',	'{}',	'pending',	'woocommerce.com',	'2024-04-07 17:44:18',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(61,	'airwallex_q1_2024',	'marketing',	'en_US',	'Save big on foreign exchange (FX) fees',	'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.',	'{}',	'pending',	'woocommerce.com',	'2024-04-07 17:44:18',	NULL,	0,	'plain',	'',	0,	0,	'info'),
